mapper.select:
select b.* from ( select a.*, row_number() over (order by a.IPOCD_DTTM desc) rnum from ( SELECT R.RCIPT_NO, R.RCIV_NO, R.IPOCD_DTTM, R.IPOCD_AMOUNT, R.IPOCD_AMOUNT_BEFORE, E.PREARNGE_AMOUNT, CASE WHEN R.PAYMTHD = 1 THEN '카드' WHEN R.PAYMTHD = 2 THEN '현금' ELSE '기타' END AS PAYMTHD, E.RCIV_AT, P.PATNT_NM FROM receipt R JOIN receive E ON R.RCIV_NO = E.RCIV_NO JOIN patient P ON E.PATNT_NO = P.PATNT_NO where 1=1 and R.IPOCD_DTTM BETWEEN #{startDate} and #{endDate} + 1
order by R.IPOCD_DTTM desc ) a ) b
where b.rnum >= #{startRow} and b.rnum <= #{endRow}

select b.* from ( select a.*, row_number() over (order by a.CLNIC_TIME desc) rnum from ( SELECT R.RCIV_NO, R.RCIV_AT, P.PATNT_NM, P.PATNT_SEXDSTN, C.CLNIC_TIME, R.PREARNGE_AMOUNT - R.RCIV_AMOUNT as missingAmount FROM RECEIVE R JOIN PATIENT P ON R.PATNT_NO = P.PATNT_NO JOIN CLINIC C ON C.CLNIC_NO = R.CLNIC_NO WHERE 1=1 AND R.RCIV_AT = 0 and C.CLNIC_TIME BETWEEN #{startDate} and #{endDate}+1
order by C.CLNIC_TIME desc ) a ) b
where b.rnum >= #{startRow} and b.rnum <= #{endRow}
