mapper.select:
select b.* from ( select a.*, row_number() over (order by a.PORDER_DATE desc) rnum from ( SELECT P.PORDER_CD, P.PORDER_CT, P.PORDER_DATE, P.PORDER_EMPNO, E.EMP_NM, P.PORDER_TOTAL, P.PORDER_CONFM, P.PORDER_CONFM_NO FROM PURCHASE_ORDER P JOIN EMPLOYEE E ON(P.PORDER_EMPNO = E.EMP_NO) where 1=1 and P.PORDER_CONFM_NO = 'A' AND P.PORDER_CONFM = 'Y' and P.PORDER_DATE BETWEEN #{startDate} and #{endDate} + 1
order by P.PORDER_DATE desc ) a ) b
where b.rnum >= #{startRow} and b.rnum <= #{endRow}

select b.* from ( select a.*, row_number() over (order by a.PORDER_CONFM_DT desc) rnum from ( SELECT P.PORDER_CD, P.PORDER_CT, P.PORDER_DATE, P.PORDER_EMPNO, E.EMP_NM, P.PORDER_TOTAL, P.PORDER_CONFM, P.PORDER_CONFM_NO, P.PORDER_CONFM_DT, (SELECT EMP_NM FROM EMPLOYEE WHERE EMP_NO = P.porder_auth_2) AS porderAuth2Nm FROM PURCHASE_ORDER P JOIN EMPLOYEE E ON(P.PORDER_EMPNO = E.EMP_NO) where 1=1 and P.PORDER_CONFM_NO = 'B' and P.PORDER_DATE BETWEEN #{startDate} and #{endDate} + 1
order by P.PORDER_CONFM_DT desc ) a ) b
where b.rnum >= #{startRow} and b.rnum <= #{endRow}
